var constants = require('constants');
let app = require('app');
let fs = app.fs;

async function get(path, view) {
  let type = app.filetypes.typeFor(path);
  let result = type.handler ? await type.handler(path) : await fs.get(path);
  result[constants.mimetype] = type.type;
  return result;
}

exports.get = get;
