var types = {};
var fs = require('fs');
function load(finder) {
  var typeFiles = fs.readdirSync("./root/system/types");



  for (var j = 0; j < typeFiles.length; j++) {
    var path = "./root/system/types/" + typeFiles[j];
    if (fs.statSync(path).isFile()) {
      var script = JSON.parse(fs.readFileSync("./root/system/types/" + typeFiles[j], "utf8"));
      for (var i = 0; i < script.length; i++) {
        var type = script[i];
        types[type.type] = type;
      }
    }
  }

  console.log(JSON.stringify(types, null, 2));
}

exports.load = load;